// src/components/OrderPanel.jsx
import React, { useState } from 'react';
import OrderItemEntry from './OrderItemEntry';
import { KHR_SYMBOL, formatKHR } from '../utils/formatters';
import { printViaBluetooth, printViaRawBT } from '../utils/bluetoothPrinter';

function OrderPanel({
    currentOrder,
    orderId,
    onUpdateQuantity,
    onClearOrder,
    onProcessPayment,
    exchangeRate,
    shopName = "·û†·û∂·ûÑ·ûõ·ûÄ·üã·ûë·üÜ·ûì·û∑·ûâ",
}) {
    const [isPrinting, setIsPrinting] = useState(false);
    const [printMethod, setPrintMethod] = useState('bluetooth'); // 'bluetooth' or 'rawbt'

    const subtotalKHR = currentOrder.reduce((sum, item) => sum + (item.priceKHR || item.priceUSD || 0) * item.quantity, 0);
    const totalKHR = subtotalKHR;

    const handlePaymentWithPrint = async () => {
        setIsPrinting(true);

        try {
            const receiptData = {
                shopName,
                orderId,
                order: currentOrder,
                totalKHR,
            };

            if (printMethod === 'bluetooth') {
                // Web Bluetooth printing (for modern browsers)
                await printViaBluetooth(receiptData);
                alert('·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö·ûî·û∂·ûì·ûá·üÑ·ûÇ·ûá·üê·ûô! ‚úÖ');
            } else {
                // RawBT app (for Android with RawBT installed)
                printViaRawBT(receiptData);
            }

            // Process payment after successful print
            onProcessPayment();

        } catch (error) {
            console.error('Print error:', error);
            
            // Show user-friendly error message
            let errorMsg = '·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ!\n\n';
            
            if (error.message.includes('Bluetooth')) {
                errorMsg += '·ûü·ûº·ûò·ûñ·û∑·ûì·û∑·ûè·üí·ûô·ûò·ûæ·ûõ:\n';
                errorMsg += '- ·ûî·ûæ·ûÄ Bluetooth\n';
                errorMsg += '- Printer ·ûó·üí·ûá·û∂·ûî·üã·ûö·ûΩ·ûÖ·û†·ûæ·ûô\n';
                errorMsg += '- ·ûî·üí·ûö·ûæ·ûÄ·ûò·üí·ûò·ûú·û∑·ûí·û∏ Chrome/Edge\n\n';
                errorMsg += '·ûÖ·ûÑ·üã·ûî·ûì·üí·ûè·ûÇ·û∑·ûè·ûõ·ûª·ûô·ûä·üÑ·ûô·ûò·û∑·ûì·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ·ûë·üÅ?';
                
                if (confirm(errorMsg)) {
                    onProcessPayment();
                }
            } else {
                alert(errorMsg + error.message);
            }
        } finally {
            setIsPrinting(false);
        }
    };

    return (
        <div className="order-panel">
            <h2>·ûî·ûâ·üí·ûá·û∏·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã·ûî·ûÖ·üí·ûÖ·ûª·ûî·üí·ûî·ûì·üí·ûì #{orderId}</h2>
            
            {/* Print method selector */}
            <div className="print-method-selector" style={{ marginBottom: '10px', padding: '8px', background: '#f5f5f5', borderRadius: '4px' }}>
                <label style={{ fontSize: '12px', marginRight: '10px' }}>
                    <input
                        type="radio"
                        value="bluetooth"
                        checked={printMethod === 'bluetooth'}
                        onChange={(e) => setPrintMethod(e.target.value)}
                        style={{ marginRight: '5px' }}
                    />
                    Bluetooth (Web)
                </label>
                <label style={{ fontSize: '12px' }}>
                    <input
                        type="radio"
                        value="rawbt"
                        checked={printMethod === 'rawbt'}
                        onChange={(e) => setPrintMethod(e.target.value)}
                        style={{ marginRight: '5px' }}
                    />
                    RawBT App
                </label>
            </div>

            <div className="current-order-items">
                {currentOrder.length === 0 ? (
                    <p className="empty-cart">·ûò·û∑·ûì·ûë·û∂·ûì·üã·ûò·û∂·ûì·ûë·üÜ·ûì·û∑·ûâ·ûÄ·üí·ûì·ûª·ûÑ·ûî·ûâ·üí·ûá·û∏·ûë·üÅ·üî</p>
                ) : (
                    currentOrder.map(item => (
                        <OrderItemEntry
                            key={item.khmerName + (item.priceKHR || item.priceUSD || 0)}
                            item={item}
                            onUpdateQuantity={onUpdateQuantity}
                        />
                    ))
                )}
            </div>
            <div className="order-summary">
                <div className="summary-line">
                    <span>·ûü·ûö·ûª·ûî·ûö·ûÑ (Subtotal):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(subtotalKHR || 0)}
                    </span>
                </div>
                <div className="summary-line total order-total">
                    <span>·ûü·ûö·ûª·ûî (Total):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(totalKHR || 0)}
                    </span>
                </div>
            </div>
            <div className="action-buttons">
                <button 
                    className="btn-clear" 
                    onClick={onClearOrder} 
                    disabled={currentOrder.length === 0 || isPrinting}
                >
                    ·ûõ·ûª·ûî·ûÄ·û∂·ûö·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã
                </button>
                <button 
                    className="btn-pay" 
                    onClick={handlePaymentWithPrint} 
                    disabled={currentOrder.length === 0 || isPrinting}
                >
                    {isPrinting ? 'üñ®Ô∏è ·ûÄ·üÜ·ûñ·ûª·ûÑ·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ...' : 'üí∞ ·ûÇ·û∑·ûè·ûõ·ûª·ûô'}
                </button>
            </div>
        </div>
    );
}

export default OrderPanel;
