// src/components/OrderPanel.jsx
import React, { useState } from 'react';
import OrderItemEntry from './OrderItemEntry';
import { KHR_SYMBOL, formatKHR } from '../utils/formatters';
import { 
  printViaRawBT,
  printViaWebBluetooth,
  generatePlainTextReceipt
} from '../utils/escposPrinter';

function OrderPanel({
    currentOrder,
    orderId,
    onUpdateQuantity,
    onClearOrder,
    onProcessPayment,
    exchangeRate,
    shopName = "·û†·û∂·ûÑ·ûõ·ûÄ·üã·ûë·üÜ·ûì·û∑·ûâ",
}) {
    const [isPrinting, setIsPrinting] = useState(false);
    const [showPreview, setShowPreview] = useState(false);
    const [receiptPreview, setReceiptPreview] = useState('');
    const [printMethod, setPrintMethod] = useState('rawbt'); // 'rawbt' or 'bluetooth'

    const subtotalKHR = currentOrder.reduce(
        (sum, item) => sum + (item.priceKHR || item.priceUSD || 0) * item.quantity, 
        0
    );
    const totalKHR = subtotalKHR;

    const handleGeneratePreview = () => {
        const receiptData = {
            shopName,
            orderId,
            order: currentOrder,
            totalKHR,
        };
        
        // Generate plain text preview
        const preview = generatePlainTextReceipt(receiptData);
        setReceiptPreview(preview);
        setShowPreview(true);
    };

    const handlePrint = async () => {
        setIsPrinting(true);

        try {
            const receiptData = {
                shopName,
                orderId,
                order: currentOrder,
                totalKHR,
            };

            if (printMethod === 'rawbt') {
                // Method 1: RawBT App (Android)
                printViaRawBT(receiptData);
                alert('‚úÖ ·ûî·û∂·ûì·ûï·üí·ûâ·ûæ·ûë·üÖ RawBT app!\n·ûü·ûº·ûò·ûñ·û∑·ûì·û∑·ûè·üí·ûô RawBT ·ûä·ûæ·ûò·üí·ûî·û∏·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ·üî');
                
            } else if (printMethod === 'bluetooth') {
                // Method 2: Web Bluetooth (Chrome/Edge)
                await printViaWebBluetooth(receiptData);
