// src/components/OrderPanel.jsx
import React from 'react';
import OrderItemEntry from './OrderItemEntry';
import { USD_SYMBOL, KHR_SYMBOL, formatUSD, formatKHR } from '../utils/formatters';

function OrderPanel({
    currentOrder,
    orderId,
    onUpdateQuantity,
    onClearOrder,
    onProcessPayment,
    exchangeRate,
    taxRate
}) {
    const subtotalUSD = currentOrder.reduce((sum, item) => sum + item.priceUSD * item.quantity, 0);
    const taxUSD = subtotalUSD * taxRate;
    const totalUSD = subtotalUSD + taxUSD;

    return (
        <div className="order-panel">
            <h2>ការបញ្ជាទិញ #{orderId}</h2>
            <div className="current-order-items">
                {currentOrder.length === 0 ? (
                    <p className="empty-cart">មិនទាន់មានទំនិញក្នុងបញ្ជី</p>
                ) : (
                    currentOrder.map(item => (
                        <OrderItemEntry
                            key={item.khmerName + item.priceUSD}
                            item={item}
                            onUpdateQuantity={onUpdateQuantity}
                        />
                    ))
                )}
            </div>
            <div className="order-summary">
                <div className="summary-line">
                    <span>សរុបរង:</span>
                    <span className="currency-value">
                        {USD_SYMBOL}{formatUSD(subtotalUSD)}
                        <span className="khr">{KHR_SYMBOL}{formatKHR(subtotalUSD * exchangeRate)}</span>
                    </span>
                </div>
                <div className="summary-line">
                    <span>ពន្ធ ({taxRate * 100}%):</span>
                    <span className="currency-value">
                        {USD_SYMBOL}{formatUSD(taxUSD)}
                        <span className="khr">{KHR_SYMBOL}{formatKHR(taxUSD * exchangeRate)}</span>
                    </span>
                </div>
                <div className="summary-line total">
                    <span>សរុបទាំងអស់:</span>
                    <span className="currency-value">
                        {USD_SYMBOL}{formatUSD(totalUSD)}
                        <span className="khr">{KHR_SYMBOL}{formatKHR(totalUSD * exchangeRate)}</span>
                    </span>
                </div>
            </div>
            <div className="action-buttons">
                <button className="btn-clear" onClick={onClearOrder}>សម្អាត</button>
                <button className="btn-pay" onClick={onProcessPayment} disabled={currentOrder.length === 0}>ទូទាត់ប្រាក់</button>
            </div>
        </div>
    );
}

export default OrderPanel;
