// src/components/Order/OrderSummary.js
import React, { useContext } from 'react';
import { OrderContext } from '../../contexts/OrderContext'; // Ensure this path is correct

const OrderSummary = () => {
    // Destructure taxRate from the context
    const { totals, formatUSD, formatKHR, USD_SYMBOL, KHR_SYMBOL, taxRate } = useContext(OrderContext);

    // Check if totals and taxRate are available
    if (!totals || typeof taxRate === 'undefined') {
        return <div>Loading summary...</div>; // Or some other placeholder
    }

    return (
        <div className="order-summary-section">
            <div className="summary-line">
                <span className="label">សរុបរង:</span>
                <span className="value">
                    {USD_SYMBOL}{formatUSD(totals.subtotalUSD)}
                    <span className="khr-value">{KHR_SYMBOL}{formatKHR(totals.subtotalKHR)}</span>
                </span>
            </div>
            <div className="summary-line">
                {/* Use taxRate directly from context */}
                <span className="label">ពន្ធ ({(taxRate * 100).toFixed(0)}%):</span>
                <span className="value">
                    {USD_SYMBOL}{formatUSD(totals.taxAmountUSD)}
                    <span className="khr-value">{KHR_SYMBOL}{formatKHR(totals.taxAmountKHR)}</span>
                </span>
            </div>
            <div className="summary-line total-line">
                <span className="label">សរុបទាំងអស់:</span>
                <span className="value">
                    {USD_SYMBOL}{formatUSD(totals.totalAmountUSD)}
                    <span className="khr-value">{KHR_SYMBOL}{formatKHR(totals.totalAmountKHR)}</span>
                </span>
            </div>
        </div>
    );
};

export default OrderSummary;
