                    orderItem.khmerName === itemData.khmerName && (orderItem.priceKHR || orderItem.priceUSD) === (itemData.priceKHR || itemData.priceUSD)
                        ? { ...orderItem, quantity: orderItem.quantity + 1 }
                        : orderItem
                );
            } else {
                return [...prevOrder, { ...itemData, quantity: 1 }];
            }
        });
    }, []);

    const updateItemQuantity = useCallback((itemName, delta) => {
        setCurrentOrder(prevOrder => {
            const itemInOrder = prevOrder.find(orderItem => orderItem.khmerName === itemName);
            if (!itemInOrder) return prevOrder;
            const newQuantity = itemInOrder.quantity + delta;
            if (newQuantity <= 0) {
                return prevOrder.filter(orderItem => orderItem.khmerName !== itemName);
            } else {
                return prevOrder.map(orderItem =>
                    orderItem.khmerName === itemName
                        ? { ...orderItem, quantity: newQuantity }
                        : orderItem
                );
            }
        });
    }, []);

    const clearOrder = useCallback(() => {
        setCurrentOrder([]);
    }, []);

    const processPayment = useCallback(() => {
        if (currentOrder.length === 0) {
            alert('·ûü·ûº·ûò·ûî·ûì·üí·ûê·üÇ·ûò·ûë·üÜ·ûì·û∑·ûâ·ûë·üÖ·ûÄ·üí·ûì·ûª·ûÑ·ûî·ûâ·üí·ûá·û∏·ûá·û∂·ûò·ûª·ûì·ûü·û∑·ûì!');
            return;
        }
        const modalElement = document.getElementById('receiptModal');
        if (modalElement) modalElement.classList.add('printing-receipt');
        setShowReceiptModal(true);
    }, [currentOrder]);

    const closeReceiptModalAndFinalizeOrder = useCallback(async () => {
        if (currentOrder.length === 0) { // Double check, though processPayment should prevent this
            setShowReceiptModal(false);
            return;
        }
        const subtotalKHR = currentOrder.reduce((sum, item) => sum + (item.priceKHR || item.priceUSD || 0) * item.quantity, 0);
        const totalKHR = subtotalKHR;

        const completedOrderDataToSave = {
            orderIdString: currentDisplayOrderId,
            items: currentOrder.map(item => ({ // ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·ûè·üÇ field ·ûä·üÇ·ûõ·ûÖ·û∂·üÜ·ûî·û∂·ûÖ·üã·ûü·ûò·üí·ûö·û∂·ûî·üã items
                khmerName: item.khmerName,
                englishName: item.englishName || '',
                priceKHR: item.priceKHR || item.priceUSD || 0,
                quantity: item.quantity,
                category: item.category // ·û¢·û∂·ûÖ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ category ·ûä·üÇ·ûö ·ûî·ûæ·ûè·üí·ûö·ûº·ûú·ûÄ·û∂·ûö·ûü·ûò·üí·ûö·û∂·ûî·üã·ûÄ·û∂·ûö·ûú·û∑·ûó·û∂·ûÇ
            })),
            subtotalKHR,
            totalKHR,
            date: serverTimestamp(), // ·ûî·üí·ûö·ûæ serverTimestamp ·ûö·ûî·ûü·üã Firebase
            exchangeRateAtPurchase: exchangeRate, // ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·û¢·ûè·üí·ûö·û∂·ûî·üí·ûè·ûº·ûö·ûî·üí·ûö·û∂·ûÄ·üã·ûñ·üÅ·ûõ Order
        };

        try {
            const docRef = await addDoc(collection(db, "orders"), completedOrderDataToSave);
            console.log("Order written to Firestore with ID: ", docRef.id);

            // ·ûü·ûò·üí·ûö·û∂·ûî·üã UI update ·ûó·üí·ûõ·û∂·ûò·üó, ·ûî·ûÑ·üí·ûÄ·ûæ·ûè object ·ûê·üí·ûò·û∏·ûá·û∂·ûò·ûΩ·ûô date ·ûá·û∂ ISO string
            const newOrderForState = {
                ...completedOrderDataToSave,
                firestoreId: docRef.id,
                date: new Date().toISOString(), // ·ûî·üí·ûö·ûæ new Date() ·ûü·ûò·üí·ûö·û∂·ûî·üã UI update ·ûó·üí·ûõ·û∂·ûò·üó
            };
            // ·ûî·ûì·üí·ûê·üÇ·ûò order ·ûê·üí·ûò·û∏·ûë·üÖ·ûÅ·û∂·ûÑ·ûä·ûæ·ûò·ûì·üÉ array (·ûü·ûò·üí·ûö·û∂·ûî·üã·ûè·ûò·üí·ûö·üÄ·ûî·ûê·üí·ûò·û∏·ûò·ûª·ûì)
            setAllOrders(prevOrders => [newOrderForState, ...prevOrders]);

        } catch (e) {
            console.error("Error adding document to Firestore: ", e);
            alert("·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ Order·üî ·ûü·ûº·ûò·ûñ·üí·ûô·û∂·ûô·û∂·ûò·ûò·üí·ûè·ûÑ·ûë·üÄ·ûè·üî Error: " + e.message);
            // ·ûò·û∑·ûì clear order ·û¨ increment counter ·ûî·ûæ save ·ûî·ûö·û∂·ûá·üê·ûô
            setShowReceiptModal(false); // ·ûî·û∑·ûë modal ·ûú·û∑·ûâ ·ûî·ûæ save ·ûî·ûö·û∂·ûá·üê·ûô
            return;
        }

        setShowReceiptModal(false);
        const modalElement = document.getElementById('receiptModal');
        if (modalElement) modalElement.classList.remove('printing-receipt');

        setCurrentOrder([]);
        setOrderIdCounter(prevCounter => prevCounter + 1);
    }, [currentOrder, currentDisplayOrderId, exchangeRate]);

    const handleSoftDeleteOrder = useCallback(async (firestoreId, deleteReason) => {
        try {
            const orderRef = doc(db, "orders", firestoreId);
            await updateDoc(orderRef, {
                isDeleted: true,
                deleteReason: deleteReason,
                deletedAt: serverTimestamp(),
            });
            // Update state to reflect the deleted order
            setAllOrders(prevOrders =>
                prevOrders.map(order =>
                    order.firestoreId === firestoreId
                        ? { ...order, isDeleted: true, deleteReason: deleteReason }
                        : order
                )
            );
            alert("Order ·ûè·üí·ûö·ûº·ûú·ûî·û∂·ûì·ûõ·ûª·ûî·ûä·üÑ·ûô·ûá·üÑ·ûÇ·ûá·üê·ûô·üî");
        } catch (error) {
            console.error("Error deleting order: ", error);
            alert("·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûõ·ûª·ûî Order: " + error.message);
        }
    }, []);

    return (
        <>
            <Header
                shopName={SHOP_NAME}
                currentExchangeRate={exchangeRate}
                onExchangeRateChange={handleExchangeRateChange}
            />

            <div className="app-navigation">
                <button
                    onClick={() => setView('pos')}
                    className={view === 'pos' ? 'active-view' : ''}
                >
                    <span role="img" aria-label="pos system">üõí</span> ·ûî·üí·ûö·ûñ·üê·ûì·üí·ûí·ûõ·ûÄ·üã (POS)
                </button>
                <button
                    onClick={() => setView('report')}
                    className={view === 'report' ? 'active-view' : ''}
                >
                    <span role="img" aria-label="sales report">üìä</span> ·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûõ·ûÄ·üã
                </button>
                <button
                    onClick={() => setView('stock')}
                    className={view === 'stock' ? 'active-view' : ''}
                >
                    <span role="img" aria-label="stock management">üì¶</span> ·ûÇ·üí·ûö·ûî·üã·ûÇ·üí·ûö·ûÑ·ûü·üí·ûè·ûª·ûÄ
                </button>
            </div>

            {isLoadingOrders && ( // Show a general loading indicator if still loading initial data
                <div className="loading-indicator full-page-loader">
                    <p>·ûÄ·üÜ·ûñ·ûª·ûÑ·ûë·û∂·ûâ·ûô·ûÄ·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô...</p>
                    {/* You can add a spinner here */}
                </div>
            )}

            {!isLoadingOrders && view === 'pos' && (
                <div className="pos-container pos-view-container">
                    <MenuPanel onAddItemToOrder={addItemToOrder} />
                    <OrderPanel
                        currentOrder={currentOrder}
                        orderId={currentDisplayOrderId}
                        onUpdateQuantity={updateItemQuantity}
                        onClearOrder={clearOrder}
                        onProcessPayment={processPayment}
                        exchangeRate={exchangeRate}
                    />
                </div>
            )}

            {!isLoadingOrders && view === 'report' && (
                <div className="pos-container report-view-container">
                     <SalesReport
                        allOrders={allOrders}
                        exchangeRate={exchangeRate}
                        onSoftDeleteOrder={handleSoftDeleteOrder}
                    />
                </div>
            )}

            {!isLoadingOrders && view === 'stock' && (
                <div className="pos-container report-view-container">
                    <StockManagement
                        stockData={stockData}
                        onUpdateStock={setStockData}
                    />
                </div>
            )}

            <ReceiptModal
                id="receiptModal"
                show={showReceiptModal}
                onClose={closeReceiptModalAndFinalizeOrder}
                order={currentOrder} // currentOrder ·ûü·ûò·üí·ûö·û∂·ûî·üã·ûî·ûÑ·üí·û†·û∂·ûâ·ûÄ·üí·ûì·ûª·ûÑ Receipt
                orderId={currentDisplayOrderId}
                exchangeRate={exchangeRate} // exchangeRate ·ûî·ûÖ·üí·ûÖ·ûª·ûî·üí·ûî·ûì·üí·ûì·ûü·ûò·üí·ûö·û∂·ûî·üã·ûî·ûÑ·üí·û†·û∂·ûâ·ûÄ·üí·ûì·ûª·ûÑ Receipt
                shopName={SHOP_NAME}
            />
        </>
    );
}

