// src/components/Menu/MenuCategory.js
import React from 'react';
import MenuItem from './MenuItem';
// import '../../assets/styles/Menu.css'; // if Menu.css contains .menu-category and .menu-items-grid styles

const MenuCategory = ({ categoryName, items }) => {
    if (!items || items.length === 0) {
        return null; // Don't render if no items for this category
    }

    return (
        <div className="menu-category">
            <h3 className="menu-category-title">{categoryName}</h3>
            <div className="menu-items-grid">
                {items.map((item, index) => (
                    // Assuming item has a unique ID from database, or use combination for key
                    // For now, using index + name for more uniqueness if names can repeat across categories but not within
                    <MenuItem key={`${item.khmerName}-${item.priceUSD}-${index}`} item={item} />
                ))}
            </div>
        </div>
    );
};

export default MenuCategory;
