// src/components/Receipt/ReceiptContent.js
import React, { useContext } from 'react';
import { OrderContext } from '../../contexts/OrderContext';

// Shop Information (can be moved to config or fetched)
const SHOP_INFO = {
    name: "ហាងកាហ្វេ របស់ខ្ញុំ",
    address: "ផ្ទះលេខ 123, ផ្លូវ 456, ភ្នំពេញ",
    tel: "012 345 678",
};

const ReceiptContent = React.forwardRef(({ orderForReceipt, orderIdForReceipt }, ref) => {
    const {
        formatUSD,
        formatKHR,
        USD_SYMBOL,
        KHR_SYMBOL,
        exchangeRateUSDToKHR, // For recalculating if needed, or use stored values
        taxRate
    } = useContext(OrderContext);

    if (!orderForReceipt || orderForReceipt.length === 0) {
        return <div ref={ref}><p>មិនមានទិន្នន័យការបញ្ជាទិញសម្រាប់វិក្កយបត្រ។</p></div>;
    }

    const now = new Date();

    // Calculate totals based on the passed orderForReceipt
    const subtotalUSD = orderForReceipt.reduce((sum, item) => sum + (item.priceUSD * item.quantity), 0);
    const taxAmountUSD = subtotalUSD * taxRate;
    const totalAmountUSD = subtotalUSD + taxAmountUSD;

    const subtotalKHR = subtotalUSD * exchangeRateUSDToKHR;
    const taxAmountKHR = taxAmountUSD * exchangeRateUSDToKHR;
    const totalAmountKHR = totalAmountUSD * exchangeRateUSDToKHR;

    // Generate QR Code Data (Example)
    const qrData = `ORDER_ID:${orderIdForReceipt};TOTAL_USD:${formatUSD(totalAmountUSD)};TOTAL_KHR:${formatKHR(totalAmountKHR)};SHOP:${SHOP_INFO.name.replace(/\s/g, '')}`;
    const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=110x110&data=${encodeURIComponent(qrData)}&ecc=M`;


    return (
        <div className="receipt-print-area" ref={ref}>
            <div className="receipt-header">
                <h3>{SHOP_INFO.name}</h3>
                <p>{SHOP_INFO.address}</p>
                <p>Tel: {SHOP_INFO.tel}</p>
                <div className="receipt-divider" style={{margin: "5px 0"}}></div>
                <p>កាលបរិច្ឆេទ: {now.toLocaleDateString('km-KH')} {now.toLocaleTimeString('km-KH', { hour: '2-digit', minute: '2-digit' })}</p>
                <p>លេខវិក្កយបត្រ: #{orderIdForReceipt}</p>
            </div>

            <table className="receipt-items-table">
                <thead>
                    <tr>
                        <th>មុខទំនិញ</th>
                        <th className="col-qty">ចំនួន</th>
                        <th className="col-price">សរុប</th>
                    </tr>
                </thead>
                <tbody>
                    {orderForReceipt.map((item, index) => (
                        <tr key={`${item.khmerName}-${index}`}>
                            <td className="receipt-item-name">
                                {item.khmerName}
                                {item.englishName && <div className="receipt-item-sub-details">({item.englishName})</div>}
                            </td>
                            <td className="col-qty">{item.quantity}</td>
                            <td className="col-price">{USD_SYMBOL}{formatUSD(item.priceUSD * item.quantity)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="receipt-double-divider"></div>

            <div className="receipt-summary">
                <div className="receipt-summary-line">
                    <span className="label">សរុបរង:</span>
                    <span className="value">
                        {USD_SYMBOL}{formatUSD(subtotalUSD)}
                        <span className="khr-val-receipt">{KHR_SYMBOL}{formatKHR(subtotalKHR)}</span>
                    </span>
                </div>
                <div className="receipt-summary-line">
                    <span className="label">ពន្ធ ({taxRate * 100}%):</span>
                    <span className="value">
                        {USD_SYMBOL}{formatUSD(taxAmountUSD)}
                        <span className="khr-val-receipt">{KHR_SYMBOL}{formatKHR(taxAmountKHR)}</span>
                    </span>
                </div>
                <div className="receipt-divider"></div>
                <div className="receipt-summary-line total-receipt-line">
                    <span className="label">សរុប (USD):</span>
                    <span className="value">{USD_SYMBOL}{formatUSD(totalAmountUSD)}</span>
                </div>
                <div className="receipt-summary-line total-receipt-line">
                    <span className="label">សរុប (KHR):</span>
                    <span className="value">{KHR_SYMBOL}{formatKHR(totalAmountKHR)}</span>
                </div>
            </div>

            <div className="receipt-qr-code">
                <p>សូមស្កេន QR Code នេះ</p>
                <img src={qrCodeUrl} alt="QR Code for Payment/Order" />
            </div>

            <div className="receipt-footer">
                <p>សូមអរគុណ! សូមអញ្ជើញមកម្តងទៀត!</p>
                <p>--- Powered by ReactPOS ---</p>
            </div>
        </div>
    );
});

export default ReceiptContent;
