// src/App.jsx
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import './App.css';
import Header from './components/Header'; // << Import Header
import MenuPanel from './components/MenuPanel';
import OrderPanel from './components/OrderPanel';
import ReceiptModal from './components/ReceiptModal';
import SalesReport from './components/SalesReport';
import { generateOrderId } from './utils/helpers';

// Constants
const DEFAULT_EXCHANGE_RATE = 4100;
const TAX_RATE = 0.10;
const SHOP_NAME = "·ûì·ûÄ·û∂·û†·üí·ûú·üÅ"; // << ·ûà·üí·ûò·üÑ·üá·û†·û∂·ûÑ·ûö·ûΩ·ûò

function App() {
    const [currentOrder, setCurrentOrder] = useState([]);
    const [orderIdCounter, setOrderIdCounter] = useState(() => {
        const savedCounter = localStorage.getItem('orderIdCounter');
        return savedCounter ? parseInt(savedCounter, 10) : 1;
    });
    const [showReceiptModal, setShowReceiptModal] = useState(false);
    const [allOrders, setAllOrders] = useState(() => {
        const savedOrders = localStorage.getItem('allOrders');
        return savedOrders ? JSON.parse(savedOrders) : [];
    });
    const [view, setView] = useState('pos'); // 'pos' or 'report'

    // State ·ûü·ûò·üí·ûö·û∂·ûî·üã·û¢·ûè·üí·ûö·û∂·ûî·üí·ûè·ûº·ûö·ûî·üí·ûö·û∂·ûÄ·üã
    const [exchangeRate, setExchangeRate] = useState(() => {
        const savedRate = localStorage.getItem('exchangeRate');
        return savedRate ? parseFloat(savedRate) : DEFAULT_EXCHANGE_RATE;
    });

    const currentDisplayOrderId = useMemo(() => generateOrderId(orderIdCounter), [orderIdCounter]);

    useEffect(() => {
        localStorage.setItem('orderIdCounter', orderIdCounter.toString());
    }, [orderIdCounter]);

    useEffect(() => {
        localStorage.setItem('allOrders', JSON.stringify(allOrders));
    }, [allOrders]);

    // ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·û¢·ûè·üí·ûö·û∂·ûî·üí·ûè·ûº·ûö·ûî·üí·ûö·û∂·ûÄ·üã·ûÄ·üí·ûì·ûª·ûÑ localStorage
    useEffect(() => {
        localStorage.setItem('exchangeRate', exchangeRate.toString());
    }, [exchangeRate]);

    // Function ·ûü·ûò·üí·ûö·û∂·ûî·üã·ûï·üí·ûõ·û∂·ûü·üã·ûî·üí·ûè·ûº·ûö·û¢·ûè·üí·ûö·û∂·ûî·üí·ûè·ûº·ûö·ûî·üí·ûö·û∂·ûÄ·üã
    const handleExchangeRateChange = useCallback((newRate) => {
        if (!isNaN(newRate) && newRate > 0) {
            setExchangeRate(newRate);
        }
    }, []);

    const addItemToOrder = useCallback((itemData) => {
        setCurrentOrder(prevOrder => {
            const existingItem = prevOrder.find(
                orderItem => orderItem.khmerName === itemData.khmerName && orderItem.priceUSD === itemData.priceUSD
            );
            if (existingItem) {
                return prevOrder.map(orderItem =>
                    orderItem.khmerName === itemData.khmerName && orderItem.priceUSD === itemData.priceUSD
                        ? { ...orderItem, quantity: orderItem.quantity + 1 }
                        : orderItem
                );
            } else {
                return [...prevOrder, { ...itemData, quantity: 1 }];
            }
        });
    }, []);

    const updateItemQuantity = useCallback((itemName, delta) => {
        setCurrentOrder(prevOrder => {
            const itemInOrder = prevOrder.find(orderItem => orderItem.khmerName === itemName);
            if (!itemInOrder) return prevOrder;
            const newQuantity = itemInOrder.quantity + delta;
            if (newQuantity <= 0) {
                return prevOrder.filter(orderItem => orderItem.khmerName !== itemName);
            } else {
                return prevOrder.map(orderItem =>
                    orderItem.khmerName === itemName
                        ? { ...orderItem, quantity: newQuantity }
                        : orderItem
                );
            }
        });
    }, []);

    const clearOrder = useCallback(() => {
        setCurrentOrder([]);
    }, []);

    const processPayment = useCallback(() => {
        if (currentOrder.length === 0) {
            alert('·ûü·ûº·ûò·ûî·ûì·üí·ûê·üÇ·ûò·ûë·üÜ·ûì·û∑·ûâ·ûë·üÖ·ûÄ·üí·ûì·ûª·ûÑ·ûî·ûâ·üí·ûá·û∏·ûá·û∂·ûò·ûª·ûì·ûü·û∑·ûì!');
            return;
        }
        const modalElement = document.getElementById('receiptModal');
        if (modalElement) modalElement.classList.add('printing-receipt');
        setShowReceiptModal(true);
    }, [currentOrder]);

    const closeReceiptModalAndFinalizeOrder = useCallback(() => {
        const subtotalUSD = currentOrder.reduce((sum, item) => sum + item.priceUSD * item.quantity, 0);
        const taxUSD = subtotalUSD * TAX_RATE;
        const totalUSD = subtotalUSD + taxUSD;

        const completedOrder = {
            id: currentDisplayOrderId,
            items: [...currentOrder],
            subtotalUSD,
            taxUSD,
            totalUSD,
            date: new Date().toISOString(), 
        };
        setAllOrders(prevOrders => [...prevOrders, completedOrder]);

        setShowReceiptModal(false);
        const modalElement = document.getElementById('receiptModal'); 
        if (modalElement) modalElement.classList.remove('printing-receipt');

        setCurrentOrder([]);
        setOrderIdCounter(prevCounter => prevCounter + 1);
    }, [currentOrder, currentDisplayOrderId]); // TAX_RATE ·ûÇ·û∫·ûá·û∂ constant ·ûò·û∑·ûì·ûè·üí·ûö·ûº·ûú·ûÄ·û∂·ûö·ûä·û∂·ûÄ·üã·ûÄ·üí·ûì·ûª·ûÑ dependency array

    return (
        <>
            {/* ·ûî·ûÑ·üí·û†·û∂·ûâ Header ·ûì·üÖ·ûë·û∏·ûì·üÅ·üá */}
            <Header
                shopName={SHOP_NAME}
                currentExchangeRate={exchangeRate}
                onExchangeRateChange={handleExchangeRateChange}
            />

            <div className="app-navigation">
                <button 
                    onClick={() => setView('pos')}
                    className={view === 'pos' ? 'active-view' : ''}
                >
                    <span role="img" aria-label="pos system">üõí</span> ·ûî·üí·ûö·ûñ·üê·ûì·üí·ûí·ûõ·ûÄ·üã (POS)
                </button>
                <button 
                    onClick={() => setView('report')}
                    className={view === 'report' ? 'active-view' : ''}
                >
                    <span role="img" aria-label="sales report">üìä</span> ·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûõ·ûÄ·üã
                </button>
            </div>

            {view === 'pos' && (
                <div className="pos-container pos-view-container">
                    <MenuPanel onAddItemToOrder={addItemToOrder} />
                    <OrderPanel
                        currentOrder={currentOrder}
                        orderId={currentDisplayOrderId}
                        onUpdateQuantity={updateItemQuantity}
                        onClearOrder={clearOrder}
                        onProcessPayment={processPayment}
                        exchangeRate={exchangeRate}
                        taxRate={TAX_RATE}
                    />
                </div>
            )}

            {view === 'report' && (
                <div className="pos-container report-view-container">
                     <SalesReport 
                        allOrders={allOrders} 
                        exchangeRate={exchangeRate} 
                        taxRate={TAX_RATE} // ·ûî·ûâ·üí·ûá·ûº·ûì taxRate ·ûë·üÖ SalesReport
                    />
                </div>
            )}

            <ReceiptModal
                id="receiptModal"
                show={showReceiptModal}
                onClose={closeReceiptModalAndFinalizeOrder}
                order={currentOrder}
                orderId={currentDisplayOrderId}
                exchangeRate={exchangeRate}
                taxRate={TAX_RATE}
                shopName={SHOP_NAME} // << ·ûî·ûâ·üí·ûá·ûº·ûì·ûà·üí·ûò·üÑ·üá·û†·û∂·ûÑ
                // ·û¢·üí·ûì·ûÄ·û¢·û∂·ûÖ·ûî·ûâ·üí·ûá·ûº·ûì SHOP_STATIC_DETAILS.address, SHOP_STATIC_DETAILS.tel ·ûñ·û∏ App.jsx ·ûò·ûÄ·ûä·üÇ·ûö ·ûî·ûæ·ûÖ·ûÑ·üã·û≤·üí·ûô·ûú·û∂ dynamic
            />
        </>
    );
}

export default App;
