// src/components/OrderPanel.jsx
import React, { useState } from 'react';
import OrderItemEntry from './OrderItemEntry';
import { KHR_SYMBOL, formatKHR } from '../utils/formatters';
import { generateReceiptImage } from '../utils/receiptGenerator';

function OrderPanel({
    currentOrder,
    orderId,
    onUpdateQuantity,
    onClearOrder,
    onProcessPayment,
    exchangeRate,
    shopName = "·û†·û∂·ûÑ·ûõ·ûÄ·üã·ûë·üÜ·ûì·û∑·ûâ",
}) {
    const [isGenerating, setIsGenerating] = useState(false);
    const [receiptImage, setReceiptImage] = useState(null);
    const [showPreview, setShowPreview] = useState(false);

    const subtotalKHR = currentOrder.reduce(
        (sum, item) => sum + (item.priceKHR || item.priceUSD || 0) * item.quantity, 
        0
    );
    const totalKHR = subtotalKHR;

    const handleGenerateReceipt = async () => {
        setIsGenerating(true);

        try {
            // Generate receipt image
            const dataURL = await generateReceiptImage({
                shopName,
                orderId,
                order: currentOrder,
                totalKHR,
            });

            setReceiptImage(dataURL);
            setShowPreview(true);

        } catch (error) {
            console.error('Error generating receipt:', error);
            alert('‚ùå ·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûî·ûÑ·üí·ûÄ·ûæ·ûè·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö!\n' + error.message);
        } finally {
            setIsGenerating(false);
        }
    };

    const handlePrintToRawBT = () => {
        if (!receiptImage) return;

        try {
            // Extract base64 data
            const base64Data = receiptImage.split(',')[1];

            // Send to RawBT app - try different URL schemes
            // Most common format for image printing in RawBT
            const rawbtURL = `rawbt:base64,${base64Data}`;
            window.location.href = rawbtURL;

            // Alternative formats if above doesn't work:
            // window.location.href = `rawbt:image,${base64Data}`;
            // window.location.href = `rawbt://print?image=${base64Data}`;

            // Process payment after short delay
            setTimeout(() => {
                setShowPreview(false);
                setReceiptImage(null);
                onProcessPayment();
            }, 1000);

        } catch (error) {
            console.error('Print error:', error);
            alert('‚ùå ·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ!\n' + error.message);
        }
    };

    const handleDownloadImage = () => {
        if (!receiptImage) return;

        const link = document.createElement('a');
        link.href = receiptImage;
        link.download = `receipt-${orderId}.png`;
        link.click();
    };

    const handleClosePreview = () => {
        setShowPreview(false);
        setReceiptImage(null);
    };

    return (
        <div className="order-panel">
            <h2>·ûî·ûâ·üí·ûá·û∏·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã·ûî·ûÖ·üí·ûÖ·ûª·ûî·üí·ûî·ûì·üí·ûì #{orderId}</h2>

            <div className="current-order-items">
                {currentOrder.length === 0 ? (
                    <p className="empty-cart">·ûò·û∑·ûì·ûë·û∂·ûì·üã·ûò·û∂·ûì·ûë·üÜ·ûì·û∑·ûâ·ûÄ·üí·ûì·ûª·ûÑ·ûî·ûâ·üí·ûá·û∏·ûë·üÅ·üî</p>
                ) : (
                    currentOrder.map(item => (
                        <OrderItemEntry
                            key={item.khmerName + (item.priceKHR || item.priceUSD || 0)}
                            item={item}
                            onUpdateQuantity={onUpdateQuantity}
                        />
                    ))
                )}
            </div>

            <div className="order-summary">
                <div className="summary-line">
                    <span>·ûü·ûö·ûª·ûî·ûö·ûÑ (Subtotal):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(subtotalKHR || 0)}
                    </span>
                </div>
                <div className="summary-line total order-total">
                    <span>·ûü·ûö·ûª·ûî (Total):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(totalKHR || 0)}
                    </span>
                </div>
            </div>

            <div className="action-buttons">
                <button 
                    className="btn-clear" 
                    onClick={onClearOrder} 
                    disabled={currentOrder.length === 0 || isGenerating}
                >
                    ·ûõ·ûª·ûî·ûÄ·û∂·ûö·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã
                </button>
                <button 
                    className="btn-pay" 
                    onClick={handleGenerateReceipt} 
                    disabled={currentOrder.length === 0 || isGenerating}
                >
                    {isGenerating ? '‚è≥ ·ûÄ·üÜ·ûñ·ûª·ûÑ·ûî·ûÑ·üí·ûÄ·ûæ·ûè...' : 'üí∞ ·ûÇ·û∑·ûè·ûõ·ûª·ûô'}
                </button>
            </div>

            {/* Receipt Preview Modal */}
            {showPreview && receiptImage && (
                <div style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    backgroundColor: 'rgba(0,0,0,0.9)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 9999,
                    padding: '20px',
                    overflow: 'auto'
                }}>
                    <div style={{
                        backgroundColor: 'white',
                        borderRadius: '16px',
                        padding: '24px',
                        maxWidth: '600px',
                        width: '100%',
                        maxHeight: '95vh',
                        display: 'flex',
                        flexDirection: 'column',
                        boxShadow: '0 20px 60px rgba(0,0,0,0.5)',
                    }}>
                        <div style={{ 
                            marginBottom: '16px', 
                            textAlign: 'center'
                        }}>
                            <h3 style={{ 
                                color: '#333',
                                fontSize: '22px',
                                marginBottom: '8px',
