// src/components/Order/OrderPanel.js
import React, { useContext } from 'react';
import { OrderContext } from '../../contexts/OrderContext';
import OrderItem from './OrderItem';
import OrderSummary from './OrderSummary';
import '../../assets/styles/Order.css'; // Import styles for this panel

const OrderPanel = ({ onProcessPayment }) => { // Pass payment handler as prop
    const { currentOrder, clearOrder, generateOrderId } = useContext(OrderContext);

    return (
        <div className="order-panel-container">
            <h2 className="order-panel-title">ការបញ្ជាទិញ #{generateOrderId()}</h2>
            
            <div className="current-order-items-list">
                {currentOrder.length === 0 ? (
                    <p className="empty-cart-message">មិនទាន់មានទំនិញក្នុងបញ្ជី</p>
                ) : (
                    currentOrder.map((item, index) => (
                        // Use a more stable key if item has a unique ID from DB
                        <OrderItem 
                            key={`${item.khmerName}-${item.priceUSD}-${index}`} 
                            item={item} 
                        />
                    ))
                )}
            </div>

            {currentOrder.length > 0 && <OrderSummary />}

            <div className="order-action-buttons">
                <button className="btn-clear-order" onClick={clearOrder} disabled={currentOrder.length === 0}>
                    សម្អាត
                </button>
                <button 
                    className="btn-process-payment" 
                    onClick={onProcessPayment} // Call the handler passed from App/POSPage
                    disabled={currentOrder.length === 0}
                >
                    ទូទាត់ប្រាក់
                </button>
            </div>
        </div>
    );
};

export default OrderPanel;
