// src/components/SalesReport.jsx
import React, { useState, useMemo } from 'react';
import { USD_SYMBOL, KHR_SYMBOL, formatUSD, formatKHR } from '../utils/formatters';

function SalesReport({ allOrders, exchangeRate }) {
    const [reportType, setReportType] = useState('daily'); // 'daily', 'monthly', 'all'
    const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]); // YYYY-MM-DD for daily
    const [filterMonth, setFilterMonth] = useState(new Date().toISOString().slice(0, 7)); // YYYY-MM for monthly

    const salesData = useMemo(() => {
        if (!allOrders || allOrders.length === 0) return { totalUSD: 0, count: 0, orders: [] };

        let filteredOrders = [];
        if (reportType === 'daily') {
            filteredOrders = allOrders.filter(order => order.date.startsWith(filterDate));
        } else if (reportType === 'monthly') {
            filteredOrders = allOrders.filter(order => order.date.startsWith(filterMonth));
        } else if (reportType === 'all') {
            filteredOrders = allOrders;
        }
        
        return {
            totalUSD: filteredOrders.reduce((sum, order) => sum + order.totalUSD, 0),
            count: filteredOrders.length,
            orders: filteredOrders,
        };
    }, [allOrders, reportType, filterDate, filterMonth]);

    const getReportTitle = () => {
        if (reportType === 'daily') return `·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûê·üí·ûÑ·üÉ·ûë·û∏ ${new Date(filterDate).toLocaleDateString('km-KH')}`;
        if (reportType === 'monthly') return `·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûÅ·üÇ ${new Date(filterMonth + '-01').toLocaleDateString('km-KH', { month: 'long', year: 'numeric' })}`;
        return '·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûõ·ûÄ·üã·ûü·ûö·ûª·ûî';
    };
    
    const handlePrintReport = () => {
        const reportPanel = document.querySelector('.sales-report-panel');
        const appContainer = document.querySelector('.pos-container.report-view-container'); // Or a more general app wrapper
        
        if (reportPanel && appContainer) {
            appContainer.classList.add('printing-sales-report'); // For overall print context
            reportPanel.classList.add('print-this-report'); // Target specific panel
            window.print();
            reportPanel.classList.remove('print-this-report');
            appContainer.classList.remove('printing-sales-report');
        } else {
            console.error("Report panel or app container not found for printing.");
        }
    };

    const handleDownloadCSV = () => {
        if (salesData.orders.length === 0) {
            alert("·ûò·û∑·ûì·ûò·û∂·ûì·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûü·ûò·üí·ûö·û∂·ûî·üã·ûë·û∂·ûâ·ûô·ûÄ·üî");
            return;
        }

        let csvContent = "data:text/csv;charset=utf-8,";
        // Headers
        csvContent += "Order ID,Date,Time,Items Count,Subtotal USD,Tax USD,Total USD,Total KHR (Approx.)\r\n";

        // Data rows
        salesData.orders.forEach(order => {
            const orderDate = new Date(order.date);
            const dateStr = orderDate.toLocaleDateString('en-CA'); // YYYY-MM-DD
            const timeStr = orderDate.toLocaleTimeString('en-GB'); // HH:MM:SS
            const itemsCount = order.items.reduce((sum, item) => sum + item.quantity, 0);
            const totalKHR = order.totalUSD * exchangeRate;

            csvContent += `${order.id},${dateStr},${timeStr},${itemsCount},${formatUSD(order.subtotalUSD)},${formatUSD(order.taxUSD)},${formatUSD(order.totalUSD)},${formatKHR(totalKHR)}\r\n`;
        });

        const encodedUri = encodeURI(csvContent);
        const link = document.createElement("a");
        link.setAttribute("href", encodedUri);
        link.setAttribute("download", `sales_report_${reportType}_${new Date().toISOString().split('T')[0]}.csv`);
        document.body.appendChild(link); 
        link.click();
        document.body.removeChild(link);
    };


    return (
        <div className="sales-report-panel">
            <h2>·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç·ûõ·ûÄ·üã</h2>
            <div className="report-controls">
                <button 
                    onClick={() => setReportType('daily')}
                    className={reportType === 'daily' ? 'active' : ''}
                >
                    ·ûî·üí·ûö·ûÖ·û∂·üÜ·ûê·üí·ûÑ·üÉ
                </button>
                {reportType === 'daily' && (
                    <input 
                        type="date" 
                        value={filterDate} 
                        onChange={(e) => setFilterDate(e.target.value)}
                        style={{padding: '7px', borderRadius:'5px', border: '1px solid var(--border-color)'}}
                    />
                )}
                <button 
                    onClick={() => setReportType('monthly')}
                    className={reportType === 'monthly' ? 'active' : ''}
                >
                    ·ûî·üí·ûö·ûÖ·û∂·üÜ·ûÅ·üÇ
                </button>
                {reportType === 'monthly' && (
                     <input 
                        type="month" 
                        value={filterMonth} 
                        onChange={(e) => setFilterMonth(e.target.value)}
                        style={{padding: '7px', borderRadius:'5px', border: '1px solid var(--border-color)'}}
                    />
                )}
                 <button 
                    onClick={() => setReportType('all')}
                    className={reportType === 'all' ? 'active' : ''}
                >
                    ·ûë·û∂·üÜ·ûÑ·û¢·ûü·üã
                </button>
            </div>

            <div className="report-actions">
                <button onClick={handlePrintReport} disabled={salesData.orders.length === 0}>
                    <span role="img" aria-label="print">üñ®Ô∏è</span> ·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ·ûö·ûî·û∂·ûô·ûÄ·û∂·ûö·ûé·üç
                </button>
                <button onClick={handleDownloadCSV} disabled={salesData.orders.length === 0}>
                    <span role="img" aria-label="download">üíæ</span> ·ûë·û∂·ûâ·ûô·ûÄ·ûá·û∂ CSV
                </button>
            </div>

            <div className="report-section" id="salesReportPrintSection">
                <h3>{getReportTitle()}</h3>
                <p>·ûÖ·üÜ·ûì·ûΩ·ûì·ûî·ûâ·üí·ûá·û∂·ûë·û∑·ûâ·ûü·ûö·ûª·ûî: {salesData.count}</p>
                <p>
                    ·ûî·üí·ûö·û∂·ûÄ·üã·ûÖ·üÜ·ûé·ûº·ûõ·ûü·ûö·ûª·ûî (USD): {USD_SYMBOL}{formatUSD(salesData.totalUSD)}
                </p>
                <p className="khr-total">
                    ·ûî·üí·ûö·û∂·ûÄ·üã·ûÖ·üÜ·ûé·ûº·ûõ·ûü·ûö·ûª·ûî (KHR): {KHR_SYMBOL}{formatKHR(salesData.totalUSD * exchangeRate)}
                </p>

                {salesData.orders.length > 0 ? (
                    <table className="report-data-table">
                        <thead>
                            <tr>
                                <th>·ûõ·üÅ·ûÅ·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö</th>
                                <th>·ûÄ·û∂·ûõ·ûî·ûö·û∑·ûÖ·üí·ûÜ·üÅ·ûë</th>
                                <th>·ûò·üâ·üÑ·ûÑ</th>
                                <th>·ûÖ·üÜ·ûì·ûΩ·ûì·ûò·ûª·ûÅ·ûë·üÜ·ûì·û∑·ûâ</th>
                                <th className="number-cell">·ûü·ûö·ûª·ûî·ûö·ûÑ (USD)</th>
                                <th className="number-cell">·ûñ·ûì·üí·ûí (USD)</th>
                                <th className="number-cell">·ûü·ûö·ûª·ûî (USD)</th>
                                <th className="number-cell">·ûü·ûö·ûª·ûî (KHR)</th>
                            </tr>
                        </thead>
                        <tbody>
                            {salesData.orders.map(order => {
                                const orderDate = new Date(order.date);
                                const itemsCount = order.items.reduce((sum, item) => sum + item.quantity, 0);
                                return (
                                    <tr key={order.id}>
                                        <td>{order.id}</td>
                                        <td>{orderDate.toLocaleDateString('km-KH')}</td>
                                        <td>{orderDate.toLocaleTimeString('km-KH')}</td>
                                        <td className="number-cell">{itemsCount}</td>
                                        <td className="number-cell">{formatUSD(order.subtotalUSD)}</td>
                                        <td className="number-cell">{formatUSD(order.taxUSD)}</td>
                                        <td className="number-cell">{formatUSD(order.totalUSD)}</td>
                                        <td className="number-cell">{formatKHR(order.totalUSD * exchangeRate)}</td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                ) : (
                    <p>·ûò·û∑·ûì·ûë·û∂·ûì·üã·ûò·û∂·ûì·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûõ·ûÄ·üã·ûü·ûò·üí·ûö·û∂·ûî·üã·ûè·ûò·üí·ûö·ûÑ·ûì·üÅ·üá·ûë·üÅ·üî</p>
                )}
            </div>
        </div>
    );
}

export default SalesReport;
