// src/firebase.js
import { initializeApp } from "firebase/app";
// import { getAnalytics } from "firebase/analytics"; // អ្នកអាច comment ចេញ បើមិនប្រើ Analytics ភ្លាមៗ
import { getFirestore, serverTimestamp } from "firebase/firestore"; // Import serverTimestamp

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyDrSEuZPwBxKHAdkFLObYWEntwwPEsRiBM",
  authDomain: "pos-nor-coffee.firebaseapp.com",
  projectId: "pos-nor-coffee",
  storageBucket: "pos-nor-coffee.firebasestorage.app", // ធានាថា .app មិនមែន .appspot.com ដូចមុន បើ Console ឲ្យបែបនេះ
  messagingSenderId: "766660732675",
  appId: "1:766660732675:web:d70ccc2435bd6733032b36",
  measurementId: "G-GCJDN2CMYG" // Optional for Firestore, used by Analytics
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
// const analytics = getAnalytics(app); // អ្នកអាច comment ចេញ បើមិនប្រើ Analytics ភ្លាមៗ
const db = getFirestore(app);

export { db, serverTimestamp }; // Export serverTimestamp សម្រាប់ប្រើក្នុង App.jsx
