// src/pages/ReportsDashboardPage.js
import React, { useState } from 'react';
import DailySalesReport from '../../components/Reports/DailySalesReport';
// You might add MonthlySalesReport component here too

const ReportsDashboardPage = () => {
    const today = new Date().toISOString().split('T')[0]; // Get today's date in YYYY-MM-DD
    const [selectedDate, setSelectedDate] = useState(today);

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
    };

    return (
        <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <header style={{marginBottom: '30px', textAlign: 'center'}}>
                <h1>ផ្ទាំងគ្រប់គ្រងរបាយការណ៍</h1>
            </header>
            
            <section className="daily-report-section">
                <h3>របាយការណ៍លក់ប្រចាំថ្ងៃ</h3>
                <div style={{ marginBottom: '20px' }}>
                    <label htmlFor="report-date" style={{ marginRight: '10px' }}>ជ្រើសរើសកាលបរិច្ឆេទ:</label>
                    <input 
                        type="date" 
                        id="report-date" 
                        value={selectedDate} 
                        onChange={handleDateChange}
                        style={{padding: '8px', borderRadius: '4px', border: '1px solid #ccc'}}
                    />
                </div>
                {selectedDate && <DailySalesReport date={selectedDate} />}
            </section>

            {/* 
            <section className="monthly-report-section" style={{marginTop: '40px'}}>
                <h3>របាយការណ៍លក់ប្រចាំខែ</h3>
                {/* Add inputs for year and month, and MonthlySalesReport component *}
            </section> 
            */}
        </div>
    );
};

export default ReportsDashboardPage;
