import { db, serverTimestamp } from '../firebase';
import { collection, addDoc } from 'firebase/firestore';
import * as XLSX from 'xlsx';

function StockManagement({ stockData, onUpdateStock }) {
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
    const [showAddForm, setShowAddForm] = useState(false);
    const [newStockItem, setNewStockItem] = useState({
        khmerName: '',
        englishName: '',
        category: 'COLD DRINKS',
        priceKHR: 0,
        quantity: 0,
    });
    const [savingStatus, setSavingStatus] = useState('');

    const categories = ['COLD DRINKS', 'HOT DRINKS', 'FOOD', 'SNACKS'];
    
    const filteredStock = Object.values(stockData);

    const handleExportExcel = () => {
        if (Object.keys(stockData).length === 0) {
            alert('·ûò·û∑·ûì·ûò·û∂·ûì·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûä·üÇ·ûõ·ûè·üí·ûö·ûº·ûú·ûõ·ûª·ûî·üî');
            return;
        }

        // Prepare data for Excel
        const excelData = Object.values(stockData).map(item => ({
            '·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·ûÅ·üí·ûò·üÇ·ûö)': item.khmerName,
            '·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·û¢·ûÑ·üã·ûÇ·üí·ûõ·üÅ·ûü)': item.englishName || '-',
            '·ûî·üí·ûö·ûó·üÅ·ûë': item.category,
            '·ûè·ûò·üí·ûõ·üÉ (KHR)': item.priceKHR,
            '·ûÖ·üÜ·ûì·ûΩ·ûì·ûü·üí·ûè·ûª·ûÄ': item.quantity,
            '·ûê·üí·ûÑ·üÉ·ûÅ·üÇ·ûÜ·üí·ûì·û∂·üÜ': item.lastUpdated || new Date().toISOString(),
        }));

        // Create worksheet and workbook
        const worksheet = XLSX.utils.json_to_sheet(excelData);
        const workbook = XLSX.utils.book_new();
        XLSX.utils.book_append_sheet(workbook, worksheet, 'Stock');

        // Set column widths
        const colWidths = [
            { wch: 20 },
            { wch: 20 },
            { wch: 15 },
            { wch: 12 },
            { wch: 12 },
            { wch: 20 }
        ];
        worksheet['!cols'] = colWidths;

        // Write to file
        XLSX.writeFile(workbook, `stock_${new Date().toISOString().split('T')[0]}.xlsx`);
    };

    const handleUpdateQuantity = (key, newQuantity) => {
        const stock = Object.values(stockData).find(item => 
            `${item.khmerName}_${item.category}` === key
        );
        
        if (stock) {
            const updatedStock = {
                ...stockData,
                [key]: {
                    ...stock,
                    quantity: Math.max(0, newQuantity),
                    lastUpdated: new Date().toISOString(),
                }
            };
            onUpdateStock(updatedStock);
        }
    };

    const handleSaveToFirebase = async () => {
        if (Object.keys(stockData).length === 0) {
            alert('·ûò·û∑·ûì·ûò·û∂·ûì·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûü·üí·ûè·ûª·ûÄ·üî');
            return;
        }

        setSavingStatus('·ûÄ·üÜ·ûñ·ûª·ûÑ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ...');
        try {
            const stockItems = Object.values(stockData).map(item => ({
                khmerName: item.khmerName,
                englishName: item.englishName,
                category: item.category,
                priceKHR: item.priceKHR,
                quantity: item.quantity,
            }));

            const docRef = await addDoc(collection(db, 'stock'), {
                date: selectedDate,
                items: stockItems,
                totalItems: stockItems.length,
                totalQuantity: stockItems.reduce((sum, item) => sum + item.quantity, 0),
                createdAt: serverTimestamp(),
                lastUpdated: serverTimestamp(),
            });
            
            setSavingStatus('·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·ûä·üÑ·ûô·ûá·üÑ·ûÇ·ûá·üê·ûô!');
            setTimeout(() => setSavingStatus(''), 2000);
            alert(`·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·ûÄ·üí·ûì·ûª·ûÑ Firestore ·ûä·üÑ·ûô·ûá·üÑ·ûÇ·ûá·üê·ûô!\nDocument ID: ${docRef.id}`);
        } catch (error) {
            setSavingStatus('·ûÄ·üÜ·û†·ûª·ûü: ' + error.message);
            console.error('Error saving to Firebase:', error);
            alert('·ûò·û∂·ûì·ûÄ·üÜ·û†·ûª·ûü·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ: ' + error.message);
        }
    };

    const handleAddNewItem = async () => {
        if (!newStockItem.khmerName.trim()) {
            alert('·ûü·ûº·ûò·ûî·ûâ·üí·ûÖ·ûº·ûõ·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ');
            return;
        }

        const key = `${newStockItem.khmerName}_${newStockItem.category}`;
        const updatedStock = {
            ...stockData,
            [key]: {
                khmerName: newStockItem.khmerName,
                englishName: newStockItem.englishName,
                category: newStockItem.category,
                priceKHR: newStockItem.priceKHR,
                quantity: newStockItem.quantity,
                lastUpdated: new Date().toISOString(),
            }
        };
        
        onUpdateStock(updatedStock);
        setNewStockItem({
            khmerName: '',
            englishName: '',
            category: 'COLD DRINKS',
            priceKHR: 0,
            quantity: 0,
        });
        setShowAddForm(false);
        alert('·ûë·üÜ·ûì·û∑·ûâ·ûê·üí·ûò·û∏·ûî·û∂·ûì·ûî·ûì·üí·ûê·üÇ·ûò·ûä·üÑ·ûô·ûá·üÑ·ûÇ·ûá·üê·ûô!');
    };
    return (
        <div className="stock-management-panel">
            <h2>·ûÇ·üí·ûö·ûî·üã·ûÇ·üí·ûö·ûÑ·ûü·üí·ûè·ûª·ûÄ</h2>
            
            {/* Date Selector and Firebase Save */}
            <div className="stock-date-controls">
                <div className="date-input-group">
                    <label>·ûá·üí·ûö·ûæ·ûü·ûö·ûæ·ûü·ûÄ·û∂·ûõ·ûî·ûö·û∑·ûÖ·üí·ûÜ·üÅ·ûë:</label>
                    <input
                        type="date"
                        value={selectedDate}
                        onChange={(e) => setSelectedDate(e.target.value)}
                        className="date-input"
                    />
                </div>
                <button onClick={handleSaveToFirebase} className="btn-firebase">üíæ ·ûö·ûÄ·üí·ûü·û∂·ûë·ûª·ûÄ·ûë·üÖ Firebase</button>
                {savingStatus && <div className="saving-status">{savingStatus}</div>}
            </div>

            {/* Import/Export Controls */}
            <div className="stock-controls">
                <div className="stock-actions">
                    <button onClick={handleExportExcel} className="btn-export">·ûë·û∂·ûâ·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûá·û∂ Excel</button>
                    <button onClick={() => setShowAddForm(!showAddForm)} className="btn-add-item">‚ûï ·ûî·ûì·üí·ûê·üÇ·ûò·ûë·üÜ·ûì·û∑·ûâ·ûê·üí·ûò·û∏</button>
                </div>

                {/* Add New Item Button - moved here */}
                {/* Add New Item Form */}
                {showAddForm && (
                    <div className="add-item-form">
                        <h3>·ûî·ûì·üí·ûê·üÇ·ûò·ûë·üÜ·ûì·û∑·ûâ·ûê·üí·ûò·û∏</h3>
                        <div className="form-row">
                            <div className="form-group">
                                <label>·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·ûÅ·üí·ûò·üÇ·ûö):</label>
                                <input
                                    type="text"
                                    value={newStockItem.khmerName}
                                    onChange={(e) => setNewStockItem({...newStockItem, khmerName: e.target.value})}
                                    placeholder="·ûß. ·ûÄ·û∂·û†·üí·ûú·üÅ·ûÅ·üí·ûò·üÖ"
                                    className="form-input"
                                />
                            </div>
                            <div className="form-group">
                                <label>·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·û¢·ûÑ·üã·ûÇ·üí·ûõ·üÅ·ûü):</label>
                                <input
                                    type="text"
                                    value={newStockItem.englishName}
                                    onChange={(e) => setNewStockItem({...newStockItem, englishName: e.target.value})}
                                    placeholder="e.g. Black Coffee"
                                    className="form-input"
                                />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>·ûî·üí·ûö·ûó·üÅ·ûë:</label>
                                <select
                                    value={newStockItem.category}
                                    onChange={(e) => setNewStockItem({...newStockItem, category: e.target.value})}
                                    className="form-input"
                                >
                                    <option value="">-- ·ûá·üí·ûö·ûæ·ûü·ûö·ûæ·ûü·ûî·üí·ûö·ûó·üÅ·ûë --</option>
                                    {categories.map(cat => (
                                        <option key={cat} value={cat}>{cat}</option>
                                    ))}
                                </select>
                            </div>
                            <div className="form-group">
                                <label>·ûè·ûò·üí·ûõ·üÉ (KHR):</label>
                                <input
                                    type="number"
                                    min="0"
                                    value={newStockItem.priceKHR}
                                    onChange={(e) => setNewStockItem({...newStockItem, priceKHR: parseFloat(e.target.value) || 0})}
                                    placeholder="0"
                                    className="form-input"
                                />
                            </div>
                            <div className="form-group">
                                <label>·ûÖ·üÜ·ûì·ûΩ·ûì·ûü·üí·ûè·ûª·ûÄ:</label>
                                <input
                                    type="number"
                                    min="0"
                                    value={newStockItem.quantity}
                                    onChange={(e) => setNewStockItem({...newStockItem, quantity: parseInt(e.target.value) || 0})}
                                    placeholder="0"
                                    className="form-input"
                                />
                            </div>
                        </div>

                        <div className="form-actions">
                            <button
                                onClick={handleAddNewItem}
                                className="btn-submit"
                            >
                                ‚ûï ·ûî·ûì·üí·ûê·üÇ·ûò
                            </button>
                            <button
                                onClick={() => {
                                    setShowAddForm(false);
                                    setNewStockItem({ khmerName: '', englishName: '', category: '', priceKHR: 0, quantity: 0 });
                                }}
                                className="btn-cancel"
                            >
                                ·ûî·üÑ·üá·ûî·ûÑ·üã
                            </button>
                        </div>
                    </div>
                )}
            </div>

            {/* Stock Table */}
            {filteredStock.length > 0 ? (
                <table className="stock-table">
                    <thead>
                        <tr>
                            <th>·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·ûÅ·üí·ûò·üÇ·ûö)</th>
                            <th>·ûà·üí·ûò·üÑ·üá·ûë·üÜ·ûì·û∑·ûâ (·û¢·ûÑ·üã·ûÇ·üí·ûõ·üÅ·ûü)</th>
                            <th>·ûî·üí·ûö·ûó·üÅ·ûë</th>
                            <th className="number-cell">·ûè·ûò·üí·ûõ·üÉ (KHR)</th>
                            <th className="number-cell">·ûÖ·üÜ·ûì·ûΩ·ûì·ûü·üí·ûè·ûª·ûÄ</th>
                            <th>·ûü·ûÄ·ûò·üí·ûò·ûó·û∂·ûñ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredStock.map(item => {
                            const key = `${item.khmerName}_${item.category}`;
                            return (
                                <tr key={key}>
                                    <td>{item.khmerName}</td>
                                    <td>{item.englishName || '-'}</td>
                                    <td>{item.category}</td>
                                    <td className="number-cell">{item.priceKHR.toLocaleString()}</td>
                                    <td className="number-cell">
                                        <input
                                            type="number"
                                            min="0"
                                            value={item.quantity}
                                            onChange={(e) => handleUpdateQuantity(key, parseInt(e.target.value) || 0)}
                                            className="stock-input"
                                        />
                                    </td>
                                    <td>
                                        <button
                                            onClick={() => handleUpdateQuantity(key, Math.max(0, item.quantity - 1))}
                                            className="btn-adjust"
                                            title="·ûê·ûô·ûÖ·ûª·üá"
                                        >
                                            -
                                        </button>
                                        <button
                                            onClick={() => handleUpdateQuantity(key, item.quantity + 1)}
                                            className="btn-adjust"
                                            title="·ûÄ·ûæ·ûì·û°·ûæ·ûÑ"
                                        >
                                            +
                                        </button>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            ) : (
                <p>·ûò·û∑·ûì·ûò·û∂·ûì·ûë·û∑·ûì·üí·ûì·ûì·üê·ûô·ûü·üí·ûè·ûª·ûÄ·üî</p>
            )}
        </div>
    );
}

export default StockManagement;
