// src/contexts/OrderContext.js
import React, { createContext, useState, useCallback, useMemo } from 'react'; // ENSURE THIS LINE IS EXACTLY AS IS
import { submitOrder as apiSubmitOrder } from '../services/api';

// 1. สร้างและ export OrderContext ที่นี่ ก่อน OrderProvider
export const OrderContext = createContext(null); // Provide a default value

export const OrderProvider = ({ children }) => {
    const [currentOrder, setCurrentOrder] = useState([]);
    const [orderCounter, setOrderCounter] = useState(1);
    const [isSubmittingOrder, setIsSubmittingOrder] = useState(false);
    const [submissionError, setSubmissionError] = useState(null);

    const USD_SYMBOL = '$';
    const KHR_SYMBOL = '៛';
    const exchangeRateUSDToKHR = 4100;
    const taxRate = 0.10; // 10%

    const formatUSD = useCallback((amount) => {
        const num = parseFloat(amount);
        return isNaN(num) ? '0.00' : num.toFixed(2);
    }, []);

    const formatKHR = useCallback((amount) => {
        const num = parseFloat(amount);
        return isNaN(num) ? '0' : Math.round(num).toLocaleString('km-KH');
    }, []);

    const addItemToOrder = useCallback((itemData) => {
        if (!itemData || typeof itemData.id === 'undefined') { // Ensure itemData and itemData.id are valid
            console.error("addItemToOrder: Invalid itemData or missing item.id", itemData);
            return;
        }
        setCurrentOrder(prevOrder => {
            const existingItemIndex = prevOrder.findIndex(
                orderItem => orderItem.id === itemData.id // Use item.id for uniqueness
            );
            if (existingItemIndex > -1) {
                const updatedOrder = [...prevOrder];
                updatedOrder[existingItemIndex].quantity++;
                return updatedOrder;
            } else {
                // Ensure priceKHR is present or calculated correctly if not from API
                const priceKHR = typeof itemData.priceKHR !== 'undefined' ? itemData.priceKHR : itemData.priceUSD * exchangeRateUSDToKHR;
                return [...prevOrder, { ...itemData, priceKHR, quantity: 1 }];
            }
        });
    }, [exchangeRateUSDToKHR]); // Added exchangeRateUSDToKHR

    const updateItemQuantity = useCallback((itemId, delta) => {
        setCurrentOrder(prevOrder => {
            const itemIndex = prevOrder.findIndex(item => item.id === itemId);
            if (itemIndex === -1) return prevOrder;

            const updatedOrder = [...prevOrder];
            const newQuantity = updatedOrder[itemIndex].quantity + delta;

            if (newQuantity <= 0) {
                updatedOrder.splice(itemIndex, 1);
            } else {
                updatedOrder[itemIndex].quantity = newQuantity;
            }
            return updatedOrder;
        });
    }, []);

    const clearOrder = useCallback(() => {
        setCurrentOrder([]);
        setSubmissionError(null);
    }, []);

    const totals = useMemo(() => {
        const subtotalUSD = currentOrder.reduce((sum, item) => {
            const price = parseFloat(item.priceUSD);
            const qty = parseInt(item.quantity, 10);
            return sum + (isNaN(price) || isNaN(qty) ? 0 : price * qty);
        }, 0);

        const taxAmountUSD = subtotalUSD * taxRate;
        const totalAmountUSD = subtotalUSD + taxAmountUSD;
        return {
            subtotalUSD, taxAmountUSD, totalAmountUSD,
            subtotalKHR: subtotalUSD * exchangeRateUSDToKHR,
            taxAmountKHR: taxAmountUSD * exchangeRateUSDToKHR,
            totalAmountKHR: totalAmountUSD * exchangeRateUSDToKHR,
        };
    }, [currentOrder, taxRate, exchangeRateUSDToKHR]);

    const generateOrderId = useCallback(() => String(orderCounter).padStart(5, '0'), [orderCounter]);

    const processAndSubmitOrder = useCallback(async (paymentMethod) => {
        if (currentOrder.length === 0) {
            setSubmissionError("មិនមានទំនិញក្នុងបញ្ជីបញ្ជាទិញទេ។");
            return null;
        }
        setIsSubmittingOrder(true);
        setSubmissionError(null);
        const orderPayload = {
            items: currentOrder.map(item => ({
                menuItemId: item.id, // Crucial: item.id must come from the API or be generated
                quantity: item.quantity,
                priceAtOrderUSD: item.priceUSD,
                priceAtOrderKHR: item.priceKHR, // Ensure this is correctly populated
            })),
            paymentMethod: paymentMethod || "មិនបានបញ្ជាក់",
        };
        try {
            const submittedOrder = await apiSubmitOrder(orderPayload);
            setIsSubmittingOrder(false);
            return submittedOrder;
        } catch (error) {
            setSubmissionError(error.message || "ការបញ្ជូនការបញ្ជាទិញបានបរាជ័យ។");
            setIsSubmittingOrder(false);
            throw error;
        }
    }, [currentOrder, exchangeRateUSDToKHR]); // Added exchangeRateUSDToKHR

    const prepareForNewOrder = useCallback(() => {
        clearOrder(); // clearOrder is now stable
        setOrderCounter(prev => prev + 1);
    }, [clearOrder]); // Dependency on clearOrder

    const value = {
        currentOrder, addItemToOrder, updateItemQuantity, clearOrder, totals,
        formatUSD, formatKHR, USD_SYMBOL, KHR_SYMBOL, orderCounter, generateOrderId,
        processAndSubmitOrder, isSubmittingOrder, submissionError, prepareForNewOrder,
        exchangeRateUSDToKHR, taxRate // Export taxRate
    };

    // Ensure OrderContext is defined before being used in OrderContext.Provider
    return <OrderContext.Provider value={value}>{children}</OrderContext.Provider>;
};
