// src/components/Menu/MenuItem.js
import React, { useContext } from 'react';
import { OrderContext } from '../../contexts/OrderContext';
// CSS is imported by MenuPanel or App.js globally, or you can import specific one:
// import '../../assets/styles/Menu.css'; // if Menu.css contains .menu-item-card styles

const MenuItem = ({ item }) => {
    const { addItemToOrder, USD_SYMBOL, KHR_SYMBOL } = useContext(OrderContext);

    const formatUSD = (amount) => parseFloat(amount).toFixed(2);
    const formatKHR = (amount) => Math.round(parseFloat(amount)).toLocaleString('km-KH');

    // item object structure: { khmerName, englishName, priceUSD, priceKHR, category, icon }
    if (!item) {
        return null; // Or some placeholder if item data is missing
    }

    return (
        <div className="menu-item-card" onClick={() => addItemToOrder(item)} title={`បន្ថែម "${item.khmerName}"`}>
            <div className="item-icon">{item.icon || '☕️'}</div>
            <div className="item-details">
                <div className="item-name-km">{item.khmerName}</div>
                {item.englishName && <div className="item-name-en">{item.englishName}</div>}
            </div>
            <div className="item-prices">
                <span className="price-usd">{USD_SYMBOL}{formatUSD(item.priceUSD)}</span>
                <span className="price-khr">{KHR_SYMBOL}{formatKHR(item.priceKHR)}</span>
            </div>
        </div>
    );
};

export default MenuItem;
