// src/components/Order/OrderItem.js
import React, { useContext } from 'react';
import { OrderContext } from '../../contexts/OrderContext';
// import '../../assets/styles/Order.css'; // Styles are imported by OrderPanel

const OrderItem = ({ item }) => {
    const { updateItemQuantity, formatUSD, formatKHR, USD_SYMBOL, KHR_SYMBOL } = useContext(OrderContext);

    if (!item) return null;

    const itemTotalUSD = item.priceUSD * item.quantity;

    return (
        <div className="order-item-entry">
            <div className="order-item-details">
                <span className="order-item-name">{item.khmerName}</span>
                <span className="order-item-price-single">
                    {USD_SYMBOL}{formatUSD(item.priceUSD)} ({KHR_SYMBOL}{formatKHR(item.priceKHR)})
                </span>
            </div>
            <div className="order-item-controls">
                <button 
                    onClick={() => updateItemQuantity(item.khmerName, item.priceUSD, -1)}
                    aria-label={`ลดจำนวน ${item.khmerName}`}
                >
                    -
                </button>
                <span className="order-item-quantity">{item.quantity}</span>
                <button 
                    onClick={() => updateItemQuantity(item.khmerName, item.priceUSD, 1)}
                    aria-label={`เพิ่มจำนวน ${item.khmerName}`}
                >
                    +
                </button>
            </div>
            <span className="order-item-total-price">
                {USD_SYMBOL}{formatUSD(itemTotalUSD)}
            </span>
        </div>
    );
};

export default OrderItem;
