// src/components/OrderPanel.jsx
import React, { useState } from 'react';
import OrderItemEntry from './OrderItemEntry';
import { KHR_SYMBOL, formatKHR } from '../utils/formatters';
import { generateReceiptImage } from '../utils/receiptGenerator';

function OrderPanel({
    currentOrder,
    orderId,
    onUpdateQuantity,
    onClearOrder,
    onProcessPayment,
    exchangeRate,
    shopName = "·û†·û∂·ûÑ·ûõ·ûÄ·üã·ûë·üÜ·ûì·û∑·ûâ",
}) {
    const [isGenerating, setIsGenerating] = useState(false);
    const [receiptImage, setReceiptImage] = useState(null);
    const [showPreview, setShowPreview] = useState(false);

    const subtotalKHR = currentOrder.reduce((sum, item) => sum + (item.priceKHR || item.priceUSD || 0) * item.quantity, 0);
    const totalKHR = subtotalKHR;

    const handleGenerateReceipt = async () => {
        setIsGenerating(true);

        try {
            // Generate receipt image
            const dataURL = await generateReceiptImage({
                shopName,
                orderId,
                order: currentOrder,
                totalKHR,
            });

            // Show preview
            setReceiptImage(dataURL);
            setShowPreview(true);

        } catch (error) {
            console.error('Error generating receipt:', error);
            alert('·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûî·ûÑ·üí·ûÄ·ûæ·ûè·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö!\n' + error.message);
        } finally {
            setIsGenerating(false);
        }
    };

    const handlePrintToRawBT = () => {
        if (!receiptImage) return;

        try {
            // Extract base64 data (remove "data:image/png;base64," prefix)
            const base64Data = receiptImage.split(',')[1];

            // Send to RawBT app with base64 image
            // Try different RawBT URL schemes
            window.location.href = `rawbt:base64,${base64Data}`;
            
            // Alternative formats if above doesn't work:
            // window.location.href = `rawbt://image?base64=${base64Data}`;
            // window.location.href = `rawbt://print/image?data=${base64Data}`;

            // Close preview and process payment
            setTimeout(() => {
                setShowPreview(false);
                setReceiptImage(null);
                onProcessPayment();
            }, 500);

        } catch (error) {
            console.error('Print error:', error);
            alert('·ûò·û∂·ûì·ûî·ûâ·üí·û†·û∂·ûÄ·üí·ûì·ûª·ûÑ·ûÄ·û∂·ûö·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ!\n' + error.message);
        }
    };

    const handleClosePreview = () => {
        setShowPreview(false);
        setReceiptImage(null);
    };

    return (
        <div className="order-panel">
            <h2>·ûî·ûâ·üí·ûá·û∏·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã·ûî·ûÖ·üí·ûÖ·ûª·ûî·üí·ûî·ûì·üí·ûì #{orderId}</h2>

            <div className="current-order-items">
                {currentOrder.length === 0 ? (
                    <p className="empty-cart">·ûò·û∑·ûì·ûë·û∂·ûì·üã·ûò·û∂·ûì·ûë·üÜ·ûì·û∑·ûâ·ûÄ·üí·ûì·ûª·ûÑ·ûî·ûâ·üí·ûá·û∏·ûë·üÅ·üî</p>
                ) : (
                    currentOrder.map(item => (
                        <OrderItemEntry
                            key={item.khmerName + (item.priceKHR || item.priceUSD || 0)}
                            item={item}
                            onUpdateQuantity={onUpdateQuantity}
                        />
                    ))
                )}
            </div>
            <div className="order-summary">
                <div className="summary-line">
                    <span>·ûü·ûö·ûª·ûî·ûö·ûÑ (Subtotal):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(subtotalKHR || 0)}
                    </span>
                </div>
                <div className="summary-line total order-total">
                    <span>·ûü·ûö·ûª·ûî (Total):</span>
                    <span className="currency-value">
                        {KHR_SYMBOL}{formatKHR(totalKHR || 0)}
                    </span>
                </div>
            </div>
            <div className="action-buttons">
                <button 
                    className="btn-clear" 
                    onClick={onClearOrder} 
                    disabled={currentOrder.length === 0 || isGenerating}
                >
                    ·ûõ·ûª·ûî·ûÄ·û∂·ûö·ûÄ·ûò·üí·ûò·üâ·ûÑ·üã
                </button>
                <button 
                    className="btn-pay" 
                    onClick={handleGenerateReceipt} 
                    disabled={currentOrder.length === 0 || isGenerating}
                >
                    {isGenerating ? '‚è≥ ·ûÄ·üÜ·ûñ·ûª·ûÑ·ûî·ûÑ·üí·ûÄ·ûæ·ûè...' : 'üí∞ ·ûÇ·û∑·ûè·ûõ·ûª·ûô'}
                </button>
            </div>

            {/* Receipt Preview Modal */}
            {showPreview && receiptImage && (
                <div 
                    style={{
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'rgba(0,0,0,0.8)',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        zIndex: 9999,
                        padding: '20px',
                    }}
                >
                    <div 
                        style={{
                            backgroundColor: 'white',
                            borderRadius: '8px',
                            padding: '20px',
                            maxWidth: '600px',
                            maxHeight: '90vh',
                            overflow: 'auto',
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <h3 style={{ marginBottom: '16px', color: '#333' }}>
                            üëÄ ·ûò·ûæ·ûõ·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö
                        </h3>
                        
                        <img 
                            src={receiptImage} 
                            alt="Receipt Preview" 
                            style={{ 
                                maxWidth: '100%', 
                                height: 'auto',
                                border: '2px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '20px',
                            }} 
                        />

                        <div style={{ display: 'flex', gap: '12px', width: '100%' }}>
                            <button
                                onClick={handleClosePreview}
                                style={{
                                    flex: 1,
                                    padding: '12px 24px',
                                    fontSize: '16px',
                                    backgroundColor: '#6c757d',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                }}
                            >
                                ‚ùå ·ûî·üÑ·üá·ûî·ûÑ·üã
                            </button>
                            <button
                                onClick={handlePrintToRawBT}
                                style={{
                                    flex: 1,
                                    padding: '12px 24px',
                                    fontSize: '16px',
                                    backgroundColor: '#28a745',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                }}
                            >
                                üñ®Ô∏è ·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ
                            </button>
                        </div>

                        <p style={{ marginTop: '12px', fontSize: '12px', color: '#666', textAlign: 'center' }}>
                            * ·ûü·ûº·ûò·ûñ·û∑·ûì·û∑·ûè·üí·ûô·ûò·ûæ·ûõ·ûú·û∑·ûÄ·üí·ûÄ·ûô·ûî·ûè·üí·ûö·ûò·ûª·ûì·ûì·ûπ·ûÑ·ûî·üÑ·üá·ûñ·ûª·ûò·üí·ûñ
                        </p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default OrderPanel;
