import React, { useState, useMemo } from 'react';
import { menuData as allMenuItems } from '../data/menuData';
import MenuItem from './MenuItem';

function MenuPanel({ onAddItemToOrder }) {
    // Get unique categories and prepend "All" option
    const uniqueCategories = useMemo(() => {
        const categorySet = new Set(allMenuItems.map(item => item.category));
        return ['ទាំងអស់', ...Array.from(categorySet)];
    }, []); // allMenuItems is from a static import, so dependency array can be empty or [allMenuItems]

    // State for the currently active category tab
    const [activeCategory, setActiveCategory] = useState(uniqueCategories[0]); // Default to "ទាំងអស់"

    // Filter menu items based on the active category
    const filteredMenuItems = useMemo(() => {
        if (activeCategory === 'ទាំងអស់') {
            return allMenuItems;
        }
        return allMenuItems.filter(item => item.category === activeCategory);
    }, [activeCategory, allMenuItems]); // Re-filter when activeCategory or allMenuItems change

    return (
        <div className="menu-panel">
            <h2>ម៉ឺនុយភេសជ្ជៈ</h2>

            {/* Category Tabs */}
            <div className="category-tabs">
                {uniqueCategories.map(categoryName => (
                    <button
                        key={categoryName}
                        className={`category-tab ${activeCategory === categoryName ? 'active' : ''}`}
                        onClick={() => setActiveCategory(categoryName)}
                    >
                        {categoryName}
                    </button>
                ))}
            </div>

            {/* Menu Items Grid */}
            {filteredMenuItems.length > 0 ? (
                <div className="menu-items-grid">
                    {filteredMenuItems.map(item => (
                        <MenuItem key={item.khmerName + item.priceUSD} item={item} onAddItemToOrder={onAddItemToOrder} />
                    ))}
                </div>
            ) : (
                <p className="empty-category-message">គ្មានទំនិញក្នុងប្រភេទនេះទេ។</p>
            )}
        </div>
    );
}

export default MenuPanel;
